import './styles.css'







class State extends React.Component{


    constructor(props){
        super(props)
        this.state = {
            msg: 0,
        }
    }

    takeOrder(){
        this.setState({
            msg: this.state.msg + 1,

        })

    }

    cancelOrder(){

            this.setState({
                msg: this.state.msg - 1,
            })






    }


    // asdf(){
